from django.contrib.auth.models import Group, User
from rest_framework import permissions, viewsets, views, response

from tutorial.quickstart.serializers import GroupSerializer, UserSerializer


class UserViewSet(viewsets.ModelViewSet):
    """
    API endpoint that allows users to be viewed or edited.
    """
    queryset = User.objects.all().order_by('-date_joined')
    serializer_class = UserSerializer
    permission_classes = [permissions.IsAuthenticated]


class GroupViewSet(viewsets.ModelViewSet):
    """
    API endpoint that allows groups to be viewed or edited.
    """
    queryset = Group.objects.all().order_by('name')
    serializer_class = GroupSerializer
    permission_classes = [permissions.IsAuthenticated]


from django.conf import settings

class Index(views.APIView):
    def get(self, request):
        bar = request.GET.get('bar', '')
        host = request.get_host()  # this will get current domain like "ecm2434-v3.onrender.com"
        scheme = 'https' if request.is_secure() else 'http'
        location_url = f'{scheme}://{host}/api/?bar={bar}'
        
        r = response.Response(data={'message': 'Hello, world!'})
        r.headers['Location'] = location_url
        return r